/**
 * 改变原数组
 * @param {number[]} flowerbed
 * @param {number} n
 * @return {boolean}
 */
var canPlaceFlowers = function (flowerbed, n) {
  // 每种一朵花，n - 1，若 n <= 0 返回 true，反之则为 false
  // 不考虑边界
  flowerbed.unshift(0);
  flowerbed.push(0);
  for (let i = 1; i < flowerbed.length - 1; i++) {
    if (
      flowerbed[i - 1] === 0 &&
      flowerbed[i] === 0 &&
      flowerbed[i + 1] === 0
    ) {
      flowerbed[i] = 1; // 种花
      n--;
    }
  }
  return n <= 0;
};

/**
 * 不改变原数组
 * @param {number[]} flowerbed
 * @param {number} n
 * @return {boolean}
 */
var canPlaceFlowers = function (flowerbed, n) {
  let i = 0;
  let len = flowerbed.length;
  while (i < len) {
    if (
      (i === 0 || flowerbed[i - 1] === 0) &&
      flowerbed[i] === 0 &&
      (i === len - 1 || flowerbed[i + 1] === 0)
    ) {
      n--;
      flowerbed[i] = 1;
    }
    i++;
  }
  return n <= 0;
};
