var trainingPlan = function (head, cnt) {
  let fast = head;
  let slow = head;
  let flag = 0;
  while (fast !== null) {
    if (flag >= cnt) {
      slow = slow.next;
    }
    fast = fast.next;
    flag++;
  }
  return slow;
};

