var findMedianSortedArrays = function (nums1, nums2) {
  let arr = nums1.concat(nums2).sort((a, b) => a - b);
  let len = arr.length;
  if (len % 2 === 0) {
    return (arr[(len >> 1) - 1] + arr[len >> 1]) / 2;
  } else {
    return arr[(len - 1) >> 1];
  }
};

