var findMedianSortedArrays = function (nums1, nums2) {
  let len1 = nums1.length,
    len2 = nums2.length;
  let len = len1 + len2;
  let mid = len >> 1;
  let i = 0,
    j = 0,
    k = 0;
  let nums = [];
  while (i < len1 && j < len2) {
    if (nums1[i] < nums2[j]) {
      nums[k++] = nums1[i++];
    } else {
      nums[k++] = nums2[j++];
    }
