  while (i < len1) {
    nums[k++] = nums1[i++];
  }
  while (j < len2) {
    nums[k++] = nums2[j++];
  }
  if (len % 2 === 0) {
    return (nums[mid - 1] + nums[mid]) / 2;
  } else {
