  // å¥‡æ•°ï¼šä¸­å¿ƒä¸º 1 ä¸ªå­—ç¬¦ï¼›å¶æ•°ï¼šä¸­å¿ƒä¸º 2 ä¸ªå­—ç¬¦
  if (!s || s.length < 2) return s || "";
  let start = 0;
  let end = 0;

  function expandAroundCenter(s, l, r) {
    while (l >= 0 && r < s.length && s[l] === s[r]) {
      l--;
      r++;
    }
    return [l + 1, r - 1];
  }

  for (let i = 0; i < s.length; i++) {
    // l = r æ—¶ï¼Œå¥‡æ•°ï¼Œä¸­å¿ƒ 1 ä¸ªå­—ç¬¦
    let [l1, r1] = expandAroundCenter(s, i, i);
    // l = r + 1 æ—¶ï¼Œå¶æ•°ï¼Œä¸­å¿ƒ 2 ä¸ªå­—ç¬¦
    let [l2, r2] = expandAroundCenter(s, i, i + 1);
    if (r1 - l1 > end - start) {
      start = l1;
      end = r1;
    }
    if (r2 - l2 > end - start) {
      start = l2;
