var canPlaceFlowers = function (flowerbed, n) {
  let i = 0;
  let len = flowerbed.length;
  while (i < len) {
    if (
      (i === 0 || flowerbed[i - 1] === 0) &&
      flowerbed[i] === 0 &&
      (i === len - 1 || flowerbed[i + 1] === 0)
    ) {
      n--;
      flowerbed[i] = 1;
    }
    i++;
  }
  return n <= 0;
};

/**
 * @param {string} str1
 * @param {string} str2
 * @return {string}
 */
var gcdOfStrings = function (str1, str2) {
  // æ±‚æœ€å¤§å…¬çº¦æ•°
  function gcd(a, b) {
    let rest = a % b;
    while (rest !== 0) {
      a = b;
      b = rest;
      rest = a % b;
    }
    return b;
  }
  if (str1 + str2 !== str2 + str1) return "";
  return str1.substring(0, gcd(str1.length, str2.length));
};

/**
 * @param {string} str1
 * @param {string} str2
 * @return {string}
 */
var gcdOfStrings = function (str1, str2) {
  // æ±‚æœ€å¤§å…¬çº¦æ•°
  function gcd(a, b) {
    let [x, y] = [a, b];
    while (y !== 0) {
      [x, y] = [y, x % y];
    }
    return x;
  }
  if (str1 + str2 !== str2 + str1) return "";
  return str1.substring(0, gcd(str1.length, str2.length));
};

/**
 * äºŒç»´åŠ¨æ€è§„åˆ’
 * @param {string} text1
 * @param {string} text2
 * @return {number}
 */
var longestCommonSubsequence = function (text1, text2) {
  let m = text1.length;
  let n = text2.length;
  let dp = new Array(m + 1).fill(0).map(() => new Array(n + 1).fill(0));
  for (let i = 1; i <= m; i++) {
    const str1 = text1[i - 1];
    for (let j = 1; j <= n; j++) {
      const str2 = text2[j - 1];
      if (str1 === str2) {
        dp[i][j] = dp[i - 1][j - 1] + 1;
      } else {
        dp[i][j] = Math.max(dp[i - 1][j], dp[i][j - 1]);
      }
    }
  }
  return dp[m][n];
};

/**
 * @param {number[]} candies
 * @param {number} extraCandies
 * @return {boolean[]}
 */
var kidsWithCandies = function (candies, extraCandies) {
  const max = Math.max.apply([], candies);
  let res = [];
  for (let i = 0; i < candies.length; i++) {
    let currentMax = candies[i] + extraCandies;
    res[i] = currentMax >= max;
  }
  return res;
};

/**
 * @param {string} word1
 * @param {string} word2
 * @return {string}
 */
var mergeAlternately = function (word1, word2) {
  let len1 = word1.length;
  let len2 = word2.length;
  if (len1 === 0) return word2;
  if (len2 === 0) return word1;

  let maxLen = Math.max(len1, len2) - 1;
  let str = "";
  for (let i = 0; i <= maxLen; i++) {
    str += (word1[i] || "") + (word2[i] || "");
  }
  return str;
};

