function getJSON(url) {
  // åˆ›å»ºä¸€ä¸ª promise å¯¹è±¡
  let promise = new Promise(function (resolve, reject) {
    let xhr = new XMLHttpRequest();
    // æ–°å»ºä¸€ä¸ª http è¯·æ±‚
    xhr.open("GET", url, true);
    // è®¾ç½®çŠ¶æ€çš„ç›‘å¬å‡½æ•°
    xhr.onreadystatechange = function () {
      if (this.readyState !== 4) return;
      // å½“è¯·æ±‚æˆåŠŸæˆ–å¤±è´¥æ—¶ï¼Œæ”¹å˜ promise çš„çŠ¶æ€
      if (this.status === 200) {
        resolve(this.response);
      } else {
        reject(new Error(this.statusText));
      }
    };
    // è®¾ç½®é”™è¯¯ç›‘å¬å‡½æ•°
    xhr.onerror = function () {
      reject(new Error(this.statusText));
    };
    // è®¾ç½®å“åº”çš„æ•°æ®ç±»åž‹
    xhr.responseType = "json";
    // è®¾ç½®è¯·æ±‚å¤´ä¿¡æ¯
    xhr.setRequestHeader("Accept", "application/json");
    // å‘é€ http è¯·æ±‚
    xhr.send(null);
  });
  return promise;
}

Function.prototype.call = function (ctx) {
  const args = [...arguments].slice(1);
  const res = null;
  ctx = ctx || window;
  ctx.fn = this;
  res = ctx.fn(...args);
  delete ctx.fn;
  return res;
};

Function.prototype.apply = function (ctx) {
  let res = null;
  // åˆ¤æ–­ context æ˜¯å¦å­˜åœ¨ï¼Œå¦‚æžœæœªä¼ å…¥åˆ™ä¸º window
  ctx = ctx || window;
  ctx.fn = this;
  res = arguments[1] ? ctx.fn(...arguments[1]) : ctx.fn();
  // å°†å±žæ€§åˆ é™¤
  delete ctx.fn;
  return res;
};

Function.prototype.bind = function (ctx, ...args) {
  const args = [...arguments].slice(1);
  const fn = this;

  return function Fn() {
    return fn.apply(this instanceof Fn ? this : ctx, args.concat(...arguments));
  };
};

function newFactory(fn, ...args) {
