const jsonstringify = (data) => {
  // ç¡®è®¤ä¸€ä¸ªå¯¹è±¡æ˜¯å¦å­˜åœ¨å¾ªçŽ¯å¼•ç”¨
  const isCyclic = (obj) => {
    // ä½¿ç”¨Setæ•°æ®ç±»åž‹æ¥å­˜å‚¨å·²ç»æ£€æµ‹è¿‡çš„å¯¹è±¡
    let stackSet = new Set();
    let detected = false;

    const detect = (obj) => {
      // ä¸æ˜¯å¯¹è±¡ç±»åž‹çš„è¯ï¼Œå¯ä»¥ç›´æŽ¥è·³è¿‡
      if (obj && typeof obj != "object") {
        return;
      }
      // å½“è¦æ£€æŸ¥çš„å¯¹è±¡å·²ç»å­˜åœ¨äºŽstackSetä¸­æ—¶ï¼Œè¡¨ç¤ºå­˜åœ¨å¾ªçŽ¯å¼•ç”¨
      if (stackSet.has(obj)) {
        return (detected = true);
      }
      // å°†å½“å‰objå­˜å¦‚stackSet
      stackSet.add(obj);

      for (let key in obj) {
        // å¯¹objä¸‹çš„å±žæ€§è¿›è¡ŒæŒ¨ä¸ªæ£€æµ‹
        if (obj.hasOwnProperty(key)) {
          detect(obj[key]);
        }
      }
      // å¹³çº§æ£€æµ‹å®Œæˆä¹‹åŽï¼Œå°†å½“å‰å¯¹è±¡åˆ é™¤ï¼Œé˜²æ­¢è¯¯åˆ¤
      /*
        ä¾‹å¦‚ï¼šå¯¹è±¡çš„å±žæ€§æŒ‡å‘åŒä¸€å¼•ç”¨ï¼Œå¦‚æžœä¸åˆ é™¤çš„è¯ï¼Œä¼šè¢«è®¤ä¸ºæ˜¯å¾ªçŽ¯å¼•ç”¨
        let tempObj = {
          name: 'å‰ç«¯èƒ–å¤´é±¼'
        }
        let obj4 = {
          obj1: tempObj,
          obj2: tempObj
        }
      */
      stackSet.delete(obj);
    };

    detect(obj);

    return detected;
  };

  // ç‰¹æ€§ä¸ƒ:
  // å¯¹åŒ…å«å¾ªçŽ¯å¼•ç”¨çš„å¯¹è±¡ï¼ˆå¯¹è±¡ä¹‹é—´ç›¸äº’å¼•ç”¨ï¼Œå½¢æˆæ— é™å¾ªçŽ¯ï¼‰æ‰§è¡Œæ­¤æ–¹æ³•ï¼Œä¼šæŠ›å‡ºé”™è¯¯ã€‚
  if (isCyclic(data)) {
    throw new TypeError("Converting circular structure to JSON");
  }

  // ç‰¹æ€§ä¹:
  // å½“å°è¯•åŽ»è½¬æ¢ BigInt ç±»åž‹çš„å€¼ä¼šæŠ›å‡ºé”™è¯¯
  if (typeof data === "bigint") {
    throw new TypeError("Do not know how to serialize a BigInt");
  }

  const type = typeof data;
  const commonKeys1 = ["undefined", "function", "symbol"];
  const getType = (s) => {
    return Object.prototype.toString
      .call(s)
      .replace(/\[object (.*?)\]/, "$1")
      .toLowerCase();
  };

  // éžå¯¹è±¡
  if (type !== "object" || data === null) {
    let result = data;
    // ç‰¹æ€§å››ï¼š
    // NaN å’Œ Infinity æ ¼å¼çš„æ•°å€¼åŠ null éƒ½ä¼šè¢«å½“åš nullã€‚
    if ([NaN, Infinity, null].includes(data)) {
      result = "null";
      // ç‰¹æ€§ä¸€ï¼š
      // `undefined`ã€`ä»»æ„çš„å‡½æ•°`ä»¥åŠ`symbolå€¼`è¢«`å•ç‹¬è½¬æ¢`æ—¶ï¼Œä¼šè¿”å›ž undefined
    } else if (commonKeys1.includes(type)) {
      // ç›´æŽ¥å¾—åˆ°undefinedï¼Œå¹¶ä¸æ˜¯ä¸€ä¸ªå­—ç¬¦ä¸²'undefined'
      return undefined;
    } else if (type === "string") {
      result = '"' + data + '"';
    }

    return String(result);
  } else if (type === "object") {
    // ç‰¹æ€§äº”:
    // è½¬æ¢å€¼å¦‚æžœæœ‰ toJSON() æ–¹æ³•ï¼Œè¯¥æ–¹æ³•å®šä¹‰ä»€ä¹ˆå€¼å°†è¢«åºåˆ—åŒ–
    // ç‰¹æ€§å…­:
    // Date æ—¥æœŸè°ƒç”¨äº† toJSON() å°†å…¶è½¬æ¢ä¸ºäº† string å­—ç¬¦ä¸²ï¼ˆåŒDate.toISOString()ï¼‰ï¼Œå› æ­¤ä¼šè¢«å½“åšå­—ç¬¦ä¸²å¤„ç†ã€‚
    if (typeof data.toJSON === "function") {
      return jsonstringify(data.toJSON());
    } else if (Array.isArray(data)) {
      let result = data.map((it) => {
        // ç‰¹æ€§ä¸€:
        // `undefined`ã€`ä»»æ„çš„å‡½æ•°`ä»¥åŠ`symbolå€¼`å‡ºçŽ°åœ¨`æ•°ç»„`ä¸­æ—¶ä¼šè¢«è½¬æ¢æˆ `null`
        return commonKeys1.includes(typeof it) ? "null" : jsonstringify(it);
      });

      return `[${result}]`.replace(/'/g, '"');
    } else {
      // ç‰¹æ€§äºŒï¼š
      // å¸ƒå°”å€¼ã€æ•°å­—ã€å­—ç¬¦ä¸²çš„åŒ…è£…å¯¹è±¡åœ¨åºåˆ—åŒ–è¿‡ç¨‹ä¸­ä¼šè‡ªåŠ¨è½¬æ¢æˆå¯¹åº”çš„åŽŸå§‹å€¼ã€‚
      if (["boolean", "number"].includes(getType(data))) {
        return String(data);
      } else if (getType(data) === "string") {
        return '"' + data + '"';
      } else {
        let result = [];
        // ç‰¹æ€§å…«
        // å…¶ä»–ç±»åž‹çš„å¯¹è±¡ï¼ŒåŒ…æ‹¬ Map/Set/WeakMap/WeakSetï¼Œä»…ä¼šåºåˆ—åŒ–å¯æžšä¸¾çš„å±žæ€§
        Object.keys(data).forEach((key) => {
          // ç‰¹æ€§ä¸‰:
          // æ‰€æœ‰ä»¥symbolä¸ºå±žæ€§é”®çš„å±žæ€§éƒ½ä¼šè¢«å®Œå…¨å¿½ç•¥æŽ‰ï¼Œå³ä¾¿ replacer å‚æ•°ä¸­å¼ºåˆ¶æŒ‡å®šåŒ…å«äº†å®ƒä»¬ã€‚
          if (typeof key !== "symbol") {
            const value = data[key];
            // ç‰¹æ€§ä¸€
            // `undefined`ã€`ä»»æ„çš„å‡½æ•°`ä»¥åŠ`symbolå€¼`ï¼Œå‡ºçŽ°åœ¨`éžæ•°ç»„å¯¹è±¡`çš„å±žæ€§å€¼ä¸­æ—¶åœ¨åºåˆ—åŒ–è¿‡ç¨‹ä¸­ä¼šè¢«å¿½ç•¥
            if (!commonKeys1.includes(typeof value)) {
              result.push(`"${key}":${jsonstringify(value)}`);
            }
          }
        });

        return `{${result}}`.replace(/'/, '"');
      }
    }
  }
};

