 */
var addTwoNumbers = function (l1, l2) {
  const node = new ListNode(0);
  let current = node;
  let curry = 0;

  while (l1 || l2) {
    const n1 = l1 ? l1.val : 0;
    const n2 = l2 ? l2.val : 0;
    const sum = n1 + n2 + curry;
    curry = parseInt(sum / 10);
    current.next = new ListNode(sum % 10);
    current = current.next;

    if (l1) {
      l1 = l1.next;
    }
    if (l2) {
      l2 = l2.next;
    }
  }

  if (curry > 0) {
    current.next = new ListNode(curry);
  }
  return node.next;
};

/**
 * 构造子串法
 * @param {string} s
 * @return {number}
 */
var lengthOfLongestSubstring = function (s) {
  let substr = "";
  let maxLen = substr.length;
  for (let i = 0; i < s.length; i++) {
    const index = substr.indexOf(s[i]);
    if (index === -1) {
      substr += s[i];
    } else {
      substr = substr.substring(index + 1) + s[i];
    }
    maxLen = Math.max(maxLen, substr.length);
  }
  return maxLen;
};

/**
 * 滑动窗口 + hash
 * @param {string} s
 * @return {number}
 */
var lengthOfLongestSubstring = function (s) {
  let map = new Map();
  let maxLen = 0;
  let l = 0;
  let r = 0;
  for (; r < s.length; r++) {
    if (map.has(s[r])) {
      l = Math.max(l, map.get(s[r]) + 1);
    }
    maxLen = Math.max(maxLen, r - l + 1);
    map.set(s[r], r);
  }
  return maxLen;
};

function lengthOfLongestSubstring(s) {
  let maxLength = 0;
  let start = 0;
  let charMap = new Map(); // 使用哈希表存储每个字符最后一次出现的位置

  for (let end = 0; end < s.length; end++) {
    const char = s[end];

    // 如果字符已存在于哈希表中，并且位置大于等于左指针，更新左指针到重复字符的下一个位置
    if (charMap.has(char) && charMap.get(char) >= start) {
      start = charMap.get(char) + 1;
    }

    // 更新当前字符在哈希表中的位置
    charMap.set(char, end);

    // 计算当前子串长度并更新最大长度
    maxLength = Math.max(maxLength, end - start + 1);
  }

  return maxLength;
}

