/**
 * 二分合并数组
 * @param {number[]} nums1
 * @param {number[]} nums2
 * @return {number}
 */
var findMedianSortedArrays = function (nums1, nums2) {
  let arr = nums1.concat(nums2).sort((a, b) => a - b);
  let len = arr.length;
  if (len % 2 === 0) {
    return (arr[(len >> 1) - 1] + arr[len >> 1]) / 2;
  } else {
    return arr[(len - 1) >> 1];
  }
};

/**
 * 二分查找不合并，直接找到中位数
 * @param {number[]} nums1
 * @param {number[]} nums2
 * @return {number}
 */
var findMedianSortedArrays = function (nums1, nums2) {
  let len1 = nums1.length,
    len2 = nums2.length;
  let len = len1 + len2;
  let mid = len >> 1;
  let i = 0,
    j = 0,
    k = 0;
  let nums = [];
  while (i < len1 && j < len2) {
    if (nums1[i] < nums2[j]) {
      nums[k++] = nums1[i++];
    } else {
      nums[k++] = nums2[j++];
    }
  }
  while (i < len1) {
    nums[k++] = nums1[i++];
  }
  while (j < len2) {
    nums[k++] = nums2[j++];
  }
  if (len % 2 === 0) {
    return (nums[mid - 1] + nums[mid]) / 2;
  } else {
    return nums[mid];
  }
};

/**
 * @param {string} s
 * @return {string}
 */
var longestPalindrome = function (s) {
  if (!s || s.length < 2) return s || "";
  let len = s.length;
  let maxLen = 1;
  let start = 0;

  const isPalindrome = (s, i, j) => {
    while (i < j) {
      if (s[i] !== s[j]) return false;
      i++;
      j--;
    }
    return true;
  };

  for (let i = 0; i < len - 1; i++) {
    for (let j = i + 1; j < len; j++) {
      if (isPalindrome(s, i, j) && j - i + 1 > maxLen) {
        maxLen = j - i + 1;
        start = i;
      }
    }
  }

  return s.substring(start, start + maxLen);
};

/**
 * @param {string} s
 * @return {string}
 */
var longestPalindrome = function (s) {
  // 奇数：中心为 1 个字符；偶数：中心为 2 个字符
  if (!s || s.length < 2) return s || "";
  let start = 0;
  let end = 0;

  function expandAroundCenter(s, l, r) {
    while (l >= 0 && r < s.length && s[l] === s[r]) {
      l--;
      r++;
    }
    return [l + 1, r - 1];
  }

  for (let i = 0; i < s.length; i++) {
    // l = r 时，奇数，中心 1 个字符
    let [l1, r1] = expandAroundCenter(s, i, i);
    // l = r + 1 时，偶数，中心 2 个字符
    let [l2, r2] = expandAroundCenter(s, i, i + 1);
    if (r1 - l1 > end - start) {
      start = l1;
      end = r1;
    }
    if (r2 - l2 > end - start) {
      start = l2;
      end = r2;
    }
  }
  return s.substring(start, end + 1);
};

