 */
var isValid = function (s) {
  let map = new Map([
    ["(", ")"],
    ["{", "}"],
    ["[", "]"],
  ]);
  let stack = [];
  for (let char of s) {
    if (map.has(char)) {
      stack.push(char);
    } else if (!stack.length || map.get(stack.pop()) !== char) {
      return false;
    }
  }
  return !stack.length;
};

