   * @param r 右侧括号已经用了几个
   * @param str 当前递归得到的拼接字符串结果
   */
  function dfs(l, r, str) {
    if (l === n && r === n) {
      return res.push(str);
    }
    // l < r 时不满足条件，剪枝
    if (l < r) return;
    // l < n 时可插入左括号，最多可以插入 n 个
    if (l < n) {
      dfs(l + 1, r, str + "(");
    }
    // r < l 时，可以插入右括号
    if (r < l) {
      dfs(l, r + 1, str + ")");
    }
  }

  dfs(0, 0, "");

  return res;
};

