var reverseKGroup = function (head, k) {
  // 翻转 start -> end 链表
  function reverseList(start, end) {
    let prev = start;
    let current = start.next;
    const node = current;
    while (current !== end) {
      let next = current.next;
      current.next = prev;
      prev = current;
      current = next;
    }
    start.next = prev;
    node.next = current;
    return node;
  }

  let dummy = new ListNode();
  dummy.next = head;
  let start = dummy;
  let end = dummy.next;
  let count = 0;

  while (end) {
    count++;
    if (count % k === 0) {
      start = reverseList(start, end.next);
      end = start.next;
    } else {
      // 移动指针
      end = end.next;
    }
  }

  return dummy.next;
};

