/**
 * @param {ListNode} head
 * @param {number} k
 * @return {ListNode}
 */
var reverseKGroup = function (head, k) {
  let n = 0;
  let current = head;
  while (current) {
    n++;
    current = current.next;
  }

  const dummy = new ListNode(0, head);
  // p0 代表反转中间段的上一个节点
  // left = 1 时，构造哨兵节点
  // 循环后，到达反转后的上一个节点
  let p0 = dummy;
  let prev = null;
  current = p0.next;

  while (n >= k) {
    n -= k;
    for (let i = 0; i < k; i++) {
      const next = current.next;
      current.next = prev;
      prev = current;
      current = next;
    }

    // 保存原始 p0 的 next
    const next = p0.next;

    // p0 指向 prev
    // p0 的 next 指向 current
    p0.next.next = current;
    p0.next = prev;
    p0 = next;
  }
  return dummy.next;
};

