var nextPermutation = function (nums) {
  // 因为 nums[i + 1] 存在，所以 i + 1 就是最后一个元素
  let i = nums.length - 2;
  while (i >= 0 && nums[i] >= nums[i + 1]) {
    i--;
  }
  // i 在数组中存在，进行交换
  if (i >= 0) {
    let j = nums.length - 1;
    while (j >= 0 && nums[i] >= nums[j]) {
      j--;
    }
    // 交换
    [nums[i], nums[j]] = [nums[j], nums[i]];
  }
  // i 不存在，说明是递减序列，则 i 是 -1
  let l = i + 1;
  let r = nums.length - 1;
  while (l < r) {
    [nums[l], nums[r]] = [nums[r], nums[l]];
    l++;
    r--;
  }
};

