var gcdOfStrings = function (str1, str2) {
  // 求最大公约数
  function gcd(a, b) {
    let rest = a % b;
    while (rest !== 0) {
      a = b;
      b = rest;
      rest = a % b;
    }
    return b;
  }
  if (str1 + str2 !== str2 + str1) return "";
  return str1.substring(0, gcd(str1.length, str2.length));
};

