      // 当请求成功或失败时，改变 promise 的状态
      if (this.status === 200) {
        resolve(this.response);
      } else {
        reject(new Error(this.statusText));
      }
    };
    // 设置错误监听函数
    xhr.onerror = function () {
      reject(new Error(this.statusText));
    };
    // 设置响应的数据类型
    xhr.responseType = "json";
    // 设置请求头信息
    xhr.setRequestHeader("Accept", "application/json");
    // 发送 http 请求
    xhr.send(null);
  });
  return promise;
}

Function.prototype.call = function (ctx) {
  const args = [...arguments].slice(1);
  const res = null;
  ctx = ctx || window;
  ctx.fn = this;
  res = ctx.fn(...args);
  delete ctx.fn;
  return res;
};

Function.prototype.apply = function (ctx) {
  let res = null;
  // 判断 context 是否存在，如果未传入则为 window
  ctx = ctx || window;
  ctx.fn = this;
  res = arguments[1] ? ctx.fn(...arguments[1]) : ctx.fn();
  // 将属性删除
  delete ctx.fn;
  return res;
};

Function.prototype.bind = function (ctx, ...args) {
  const args = [...arguments].slice(1);
  const fn = this;

  return function Fn() {
    return fn.apply(this instanceof Fn ? this : ctx, args.concat(...arguments));
  };
};

function newFactory(fn, ...args) {
  // method1
  const obj = new Object();
  obj.__proto__ = newFactory.prototype;
  newFactory.apply(obj, args);
  return obj;
}

function newFactory(fn, ...args) {
  const obj = Object.create(fn.prototype);
  fn.apply(obj, args);
  return obj;
}

function _instanceof(target, origin) {
  if (typeof target !== "object" || target === null) return false;
  if (typeof origin !== "function") {
    throw new TypeError("origin must be function");
  }
  let proto = Object.getPrototypeOf(target); // 相当于: proto = target.__proto__
  while (proto) {
    if (proto === origin.prototype) return true;
    proto = Object.getPrototypeOf(proto);
  }
  return false;
}

function _setTimeout(fn, timeout) {
  let timer;
  let startTime = Date.now();
  const loop = () => {
    timer = window.requestAnimationFrame(loop);
    if (Date.now() - startTime >= timeout) {
      fn.call(this, timer);
      window.cancelAnimationFrame(timer);
    }
  };
  window.requestAnimationFrame(loop);
}

function _setInterval(fn, interval) {
  let timer;
  let startTime = Date.now();
  const loop = () => {
    timer = window.requestAnimationFrame(loop);
    if (Date.now() - startTime >= interval) {
      fn.call(this, timer);
      startTime = Date.now();
    }
  };
  timer = window.RequestAnimationFrame(loop);
  return timer;
}

const _setInterval = (fn, interval) => {
  let timer;
  const loop = () => {
    timer = setTimeout(() => {
      // timeout 时间之后会执行真正的函数 fn
      fn();
      // 同时再次调用 interval 本身
      loop();
    }, interval);
  };
  // 开始执行
  loop();
  // 返回用于关闭定时器的函数
  return () => clearTimeout(timer);
};

