var Solution = function (nums) {
  this.nums = nums;
  this.raw = [...this.nums];
};

/**
 * @return {number[]}
 */
Solution.prototype.reset = function () {
  return this.raw;
};

/**
 * @return {number[]}
 */
Solution.prototype.shuffle = function () {
  let res = [];
  let arr = [...this.nums];
  while (arr.length > 0) {
    let idx = Math.floor(Math.random() * arr.length);
    res.push(arr.splice(idx, 1));
  }
  return res;
};

