Promise.allSettled = function(list) {
  return new Promise((resolve, reject) => {
    let count = 0 // 计数器
    let result = [] // 存放结果
    let len = list.length

    if (!len)
      return []

    for (let item of list) {
      // 有的数据项可能不是 promise，因此需要包装一下
      Promise.resolve(item)
        .then(val => {
          count++
          // 成功属性设置
          result[i] = {
            status: "fulfilled",
            value: val
          }
          // 当所有的 promise 都成功后，返回结果
          if (count === len) {
            resolve(result)
          }
        })
        .catch(err => {
          count++
          // 失败属性设置
          result[i] = {
            status: "rejected",
            reason: err
          }
          // 当所有的 promise 都失败后，返回结果
          if (count === len) {
            reject(result)
          }
        })
    }
  })
}

