      for (let item of promiseList) {
        _Promise.resolve(item)
          .then(res => {
            count += 1
            result[i] = {
              status: "fulfilled",
              value: res
            }
            if (count === len) {
              resolve(result)
            }
          })
          .catch(err => {
            count += 1
            result[i] = {
              status: "rejected",
              reason: err
            }
            if (count === len) {
              reject(result)
            }
          })
      }
    })
  }
  static race(promiseList) {
    return new _Promise((resolve, reject) => {
      let count = 0
