            }
          })
          .catch(err => {
            count += 1
            result[i] = {
              status: "rejected",
              reason: err
            }
            if (count === len) {
              reject(result)
            }
          })
      }
    })
  }
  static any(promiseList) {
    return new _Promise((resolve, reject) => {
      let count = 0
      let len = promiseList.length

      if (!len) {
        return
      }

      for (let item of promiseList) {
        _Promise.resolve(item)
          .then(res => resolve(res))
          .catch(err => {
            count += 1
            if (count === len) {
