/**
 * @param {number[]} nums
 * @return {number[]}
 */
var productExceptSelf = function (nums) {
  let len = nums.length;
  const L = new Array(len);
  const R = new Array(len);
  const ans = new Array(len);

  // L[i] 表示 i 左侧所有元素乘积
  L[0] = 1;
  for (let i = 1; i < len; i++) {
    L[i] = L[i - 1] * nums[i - 1];
  }

  // R[i] 表示 i 右侧所有元素乘积
  R[len - 1] = 1;
  for (let i = len - 2; i >= 0; i--) {
    R[i] = R[i + 1] * nums[i + 1];
  }

  // 除 i 外其他元素乘积 = L * R
  for (let i = 0; i < len; i++) {
    ans[i] = L[i] * R[i];
  }

  return ans;
};

/**
 * @param {number[]} nums
 * @return {number[]}
 */
var productExceptSelf = function (nums) {
  // 将 L 或 R 数组用输出数组来计算
  let len = nums.length;
  let ans = new Array(len);

  // ans[i] 表示 i 左侧所有元素的乘积，刚开始 i = 0 左侧无元素，因此 = 1
  ans[0] = 1;
  for (let i = 1; i < nums.length; i++) {
    ans[i] = ans[i - 1] * nums[i - 1];
  }
  // R 为右侧所有元素的乘积，刚开始右边无元素，因此 = 1
  let R = 1;
  for (let i = len - 1; i >= 0; i--) {
    // 对于索引 i，左边乘积为 ans[i]，右边为 R
    ans[i] *= R;
    // R 需要包含右边所有乘积，所以要累乘
    R *= nums[i];
  }
  return ans;
};
