const _setInterval = (fn, interval) => {
  let timer;
  const loop = () => {
    timer = setTimeout(() => {
      // timeout 时间之后会执行真正的函数 fn
      fn();
      // 同时再次调用 interval 本身
      loop();
    }, interval);
  };
  // 开始执行
  loop();
  // 返回用于关闭定时器的函数
  return () => clearTimeout(timer);
};

