function isObject(val) {
  return typeof val === 'object' && val !== null
}

function shallowCopy(obj) {
  if (!isObject(obj)) return
  let newObj = obj instanceof Array ? [] : {}
  for (let key in obj) {
    if (obj.hasOwnProperty(key)) {
      newObj[key] = obj[key]
    }
  }
  return newObj
}

