// 只考虑普通对象属性，不考虑内置对象和函数
function deepCopy(obj) {
  if (!obj || typeof obj !== "object") return
  let newObj = obj instanceof Array ? [] : {}
  for (let k in obj) {
    if (obj.hasOwnProperty(k) && typeof obj[k] === "object") {
      newObj[k] = deepCopy(obj[k])
    } else {
      newObj[k] = obj[k]
    }
  }
  return newObj
}

