def fact_call(n):
    return fact_inter(n, 1)


def fact_inter(num, product):
    if num == 1:
        return product
    return fact_inter(num - 1, num * product)


print(fact_inter(5, 1))
print(fact_inter(4, 5))
print(fact_inter(3, 20))
print(fact_inter(2, 60))
print(fact_inter(1, 120))

def move(n, a, b, c):
    if n == 1:
        print(a, "->", c)
    else:
        move(n - 1, a, c, b)
        print(a, "->", "c")
        move(n - 1, b, a, c)


