/**
 * 堆排序 - 递归实现
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var findKthLargest = function (nums, k) {
  function heapify(arr, index, size) {
    let max = index;
    let l = index * 2 + 1;
    let r = index * 2 + 2;

    if (l < size && arr[l] > arr[index]) {
      max = l;
    }
    if (r < size && arr[r] > arr[max]) {
      max = r;
    }
    if (max !== index) {
      [arr[index], arr[max]] = [arr[max], arr[index]];
      heapify(arr, max, size);
    }
  }

  function heapSort(arr) {
    // 最大值构建
    let size = arr.length;
    for (let i = size; i >= 0; i--) {
      heapify(arr, i, size);
    }

    while (size > 1) {
      size--;
      [arr[0], arr[size]] = [arr[size], arr[0]];
      heapify(arr, 0, size);
    }

    return arr;
  }

  return heapSort(nums)[nums.length - k];
};

/**
 * 快排
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var findKthLargest = function (nums, k) {
  function quickSort(arr, left = 0, right = arr.length - 1) {
    if (left < right) {
      let pivot = arr[(left + right) >> 1];
      let i = left;
      let j = right;
      while (i <= j) {
        while (i <= j && arr[i] < pivot) i++;
        while (i <= j && arr[j] > pivot) j--;
        if (i <= j) {
          [arr[i], arr[j]] = [arr[j], arr[i]];
          i++;
          j--;
        }
      }
      quickSort(arr, i, right);
      quickSort(arr, left, j);
    }
    return arr;
  }
  return quickSort(nums)[nums.length - k];
};

/**
 * 快排 - 栈溢出
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var findKthLargest = function (nums, k) {
  const quickSort = (arr) => {
    if (arr.length <= 1) return arr;
    const middleIndex = arr.length >> 1;
    const middle = arr.splice(middleIndex, 1)[0];
    let left = [];
    let right = [];
    for (let i = 0; i < arr.length; i++) {
      if (arr[i] < middle) {
        left.push(arr[i]);
      } else {
        right.push(arr[i]);
      }
    }
    return quickSort(left).concat(middle, quickSort(right));
  };
  const result = quickSort(nums);
  return result[result.length - k];
};
