/**
 * 二分合并数组
 * @param {number[]} nums1
 * @param {number[]} nums2
 * @return {number}
 */
var findMedianSortedArrays = function (nums1, nums2) {
  let arr = nums1.concat(nums2).sort((a, b) => a - b);
  let len = arr.length;
  if (len % 2 === 0) {
    return (arr[(len >> 1) - 1] + arr[len >> 1]) / 2;
  } else {
    return arr[(len - 1) >> 1];
  }
};

/**
 * 二分查找不合并，直接找到中位数
 * @param {number[]} nums1
 * @param {number[]} nums2
 * @return {number}
 */
var findMedianSortedArrays = function (nums1, nums2) {
  let len1 = nums1.length,
    len2 = nums2.length;
  let len = len1 + len2;
  let mid = len >> 1;
  let i = 0,
    j = 0,
    k = 0;
  let nums = [];
  while (i < len1 && j < len2) {
    if (nums1[i] < nums2[j]) {
      nums[k++] = nums1[i++];
    } else {
      nums[k++] = nums2[j++];
    }
  }
  while (i < len1) {
    nums[k++] = nums1[i++];
  }
  while (j < len2) {
    nums[k++] = nums2[j++];
  }
  if (len % 2 === 0) {
    return (nums[mid - 1] + nums[mid]) / 2;
  } else {
    return nums[mid];
  }
};
