/**
 * @param {string} s
 * @return {number}
 */
var longestValidParentheses = function (s) {
  let maxLength = 0;
  let dp = new Array(s.length).fill(0);
  for (let i = 0; i < s.length; i++) {
    if (s[i] === ")") {
      if (s[i - 1] === "(") {
        dp[i] = (i >= 2 ? dp[i - 2] : 0) + 2;
      } else if (
        s[i - 1] === ")" &&
        i > dp[i - 1] &&
        s[i - 1 - dp[i - 1]] === "("
      ) {
        dp[i] =
          (i - dp[i - 1] >= 2 ? dp[i - dp[i - 1] - 2] : 0) + dp[i - 1] + 2;
      }
    }
    maxLength = Math.max(maxLength, dp[i]);
  }
  return maxLength;
};
