function throttle(fn, wait) {
  let timer
  let startTime = Date.now()
  return function(...args) {
    let currentTime = Date.now()
    let remainingTime = wait - (currentTime - startTime)
    let context = this
    if (timer) clearTimeout(timer)
    if (remainingTime <= 0) {
      fn.apply(context, args)
      startTime = Date.now()
    } else {
      timer = setTimeout(fn, remainingTime)
    }
  }
}

/**
* @desc fn 回调函数
* @desc wait 等待事件
* @desc immediate 是否立即执行一次
* @desc trail 执行结束后是否再执行一次
*/
function throttle(fn, wait, immediate, trail) {
  let timer
  let startTime = 0
  let clearTimer = () => {
    clearTimeout(timer)
    timer = null
  }
  let fn = function(...args) {
    let context = this
    if (!prev && !immediate) {
      startTime = Date.now()
    }
    let remainingTime = wait - (Date.now() - startTime)
    if (timer) clearTimer()
    if (!timer) {
      if (remainingTime <= 0 || remainingTime > wait) {
        startTime = Date.now()
        fn.apply(context, args)
      }
      if (trail) {
        timer = setTimeout(() => {
          startTime = immediate ? 0 : Date.now()
          timer = null
          fn.apply(context, args)
        }, remainingTime)
      }
    }
  }
  fn.off = function() {
    clearTimer()
    startTime = 0
  }
  return fn
}
