  if (!headA || !headB) return null;
  let pA = headA;
  let pB = headB;
  while (pA !== pB) {
    pA = pA ? pA.next : headB;
    pB = pB ? pB.next : headA;
  }
  return pA;
};

/**
 * Definition for singly-linked list.
 */
function ListNode(val) {
  this.val = val;
  this.next = null;
}

/**
 * @param {ListNode} headA
 * @param {ListNode} headB
 * @return {ListNode}
 */
var getIntersectionNode = function (headA, headB) {
  if (!headA || !headB) return null;
  let map = new Map();
  let pA = headA;
  let pB = headB;
  while (pA) {
    map.set(pA, 1);
    pA = pA.next;
  }
  while (pB) {
    if (map.has(pB)) return pB;
    pB = pB.next;
  }
};
