/**
 * @param {number} n
 * @return {number}
 */
var fib = function (n) {
  if (n === 0 || n === 1) return n;
  return fib(n - 1) + fib(n - 2);
};

/**
 * @param {number} n
 * @return {number}
 */
var fib = function (n, a = 0, b = 1) {
  if (n === 0) return a;
  return fib(n - 1, b, a + b);
};

/**
 * @param {number} n
 * @return {number}
 */
var fib = function (n) {
  let arr = [0, 1];
  for (let i = 2; i <= n; i++) {
    arr.push(arr[i - 1] + arr[i - 2]);
  }
  return arr[n];
};

/**
 * 迭代
 * @param {number} n
 * @return {number}
 */
var fib = function (n) {
  let a = 0;
  let b = 1;
  while (n--) {
    [a, b] = [b, a + b];
  }
  return a;
};
