import{_ as t,b as d,o as l,a8 as a}from"./chunks/framework.BEMRIPir.js";const p=JSON.parse('{"title":"科目一","description":"","frontmatter":{},"headers":[],"relativePath":"daily/driving-test/subject1.md","filePath":"daily/driving-test/subject1.md"}'),i={name:"daily/driving-test/subject1.md"},e=a('<h1 id="科目一" tabindex="-1">科目一 <a class="header-anchor" href="#科目一" aria-label="Permalink to &quot;科目一&quot;">​</a></h1><h2 id="车辆代号" tabindex="-1">车辆代号 <a class="header-anchor" href="#车辆代号" aria-label="Permalink to &quot;车辆代号&quot;">​</a></h2><table><thead><tr><th>代号</th><th>准驾车型</th><th>准驾的车辆</th><th>准予驾驶的其他准驾车型</th></tr></thead><tbody><tr><td>A1</td><td class="text-primary font-bold">大型客车</td><td>大型载客汽车（车长大于6米或核定载客人数大于等于20人的载客汽车）</td><td>A3、B1、B2、C1、C2、C3、C4、C5<sup>1</sup>、M</td></tr><tr><td>A2</td><td class="text-primary font-bold">重型牵引挂车</td><td>总质量大于4500kg的汽车列车</td><td>B1、B2、C1、C2、C3、C4、C5<sup>1</sup>、C6、M</td></tr><tr><td>A3</td><td class="text-primary font-bold">公交车</td><td>核准载10人以上的城市公共汽车</td><td>C1、C2、C3、C4、C5<sup>1</sup></td></tr><tr><td>B1</td><td class="text-primary font-bold">中型客车</td><td>中型载客汽车（含核准载10人以上、19人以下的城市公共汽车）</td><td>C1、C2、C3、C4、C5<sup>1</sup>、M</td></tr><tr><td>B2</td><td class="text-primary font-bold">大型货车</td><td><ul><li>重型、中型载货汽车</li><li>大、重、中型专项作业车</li></ul></td><td>C1、C2、C3、C4、C5<sup>1</sup>、M</td></tr><tr><td>C1</td><td class="text-primary font-bold">小型汽车</td><td><ul><li>小型、微型载客汽车</li><li>轻型、微型载货汽车</li><li>轻型、微型专项作业车</li></ul></td><td>C2、C3、C4、C5<sup>1</sup></td></tr><tr><td>C2</td><td class="text-primary font-bold">小型自动挡汽车</td><td><ul><li>小型、微型自动挡载客汽车</li><li>轻型、微型自动挡载货汽车</li><li>轻型、微型自动挡专项作业车</li><li>上肢残疾人专用小型自动挡载客汽车</li></ul></td><td>C5<sup>1</sup></td></tr><tr><td>C3</td><td class="text-primary font-bold">低速载货汽车</td><td>低速载货汽车（原四轮农用运输车）</td><td>C4</td></tr><tr><td>C4</td><td>三轮汽车</td><td>三轮汽车（原三轮农用运输车）</td><td>无</td></tr><tr><td>C5</td><td>残疾人专用小型自动挡载客汽车</td><td>残疾人专用小型、微型自动挡载客汽车（允许上肢、右下肢或者双下肢残疾人驾驶）</td><td>无</td></tr><tr><td>C6<sup>2</sup></td><td class="text-primary font-bold">轻型牵引挂车</td><td>总质量小于（不包含等于）4500kg的汽车列车</td><td>无</td></tr><tr><td>D</td><td>普通三轮摩托车</td><td>发动机排量大于50毫升或者最大设计车速大于50km/h的三轮摩托车</td><td>E、F</td></tr><tr><td>E</td><td>普通二轮摩托车</td><td>发动机排量大于50毫升或者最大设计车速大于50km/h的二轮摩托车</td><td>F</td></tr><tr><td>F</td><td>轻便摩托车</td><td>发动机排量小于等于50毫升，最大设计车速小于等于50km/h的摩托车</td><td>无</td></tr><tr><td>M</td><td>轮式自行机械车</td><td>轮式自行机械车</td><td>无</td></tr><tr><td>N</td><td>无轨电车</td><td>无轨电车</td><td>无</td></tr><tr><td>P</td><td>有轨电车</td><td>有轨电车</td><td>无</td></tr></tbody></table><h2 id="驾驶证申请" tabindex="-1">驾驶证申请 <a class="header-anchor" href="#驾驶证申请" aria-label="Permalink to &quot;驾驶证申请&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">口诀</p><ol><li>考试中 1 年，取得后 3 年</li><li>假 1 撤 3 醉 5 逃终生</li><li>年满 20 可申领：城、公、大货、有无轨</li><li>无标扣车不扣证</li></ol></div><h2 id="罚款" tabindex="-1">罚款 <a class="header-anchor" href="#罚款" aria-label="Permalink to &quot;罚款&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">口诀</p><ol><li>假材料/隐瞒 500 以下；作弊 1000 以下；欺骗 2000 以下；</li><li>逃逸不犯罪 2000</li><li>代罚 3倍 5万；代审 3倍 2万</li></ol></div><table><thead><tr><th>区间</th><th>罚款金额</th><th>原因</th></tr></thead><tbody><tr><td rowspan="2">200 区间</td><td>20元～200元</td><td><ul><li>补领新证用原证</li><li>实习期单独上高速</li><li>实习期未贴实习标志</li><li>特殊车辆驾驶人信息变更30天内未备案</li></ul></td></tr><tr><td>200元以下</td><td>车辆转让未登记</td></tr><tr><td rowspan="2">500 区间</td><td>200元～500元</td><td><ul><li>驾驶证被扣留期间，采用欺骗手段补领驾驶证</li><li>身体条件发生变化不适合驾车仍驾车</li><li>逾期仍参加审验仍驾车</li></ul></td></tr><tr><td>500元以下</td><td> 提供虚假材料申领驾驶证 </td></tr><tr><td>1k 区间</td><td>1k元以下</td><td>参加安全教育记分中造假</td></tr><tr><td rowspan="3">2k 区间</td><td>200元～2k元</td><td><ul><li>肇事逃逸，尚不构成犯罪</li><li>未取得驾驶证/驾驶证被吊销/机动车驾驶证被暂扣期间驾车</li><li>超速50%（同时扣12分）</li><li>驾驶拼装车/报废车</li><li>未悬挂机动车号牌/未放置检验合格标志、保险标志/未随车携带行驶证、驾驶证</li><li>故意遮挡、污损/不按规定安装机动车号牌</li><li>违反交通管制的规定强行通行，不听劝阻</li><li>非法拦截/扣留机动车</li><li>故意损毁/移动/涂鸦交通设施，造成危害后果，尚不构成犯罪</li></ul></td></tr><tr><td>1k元～2k元</td><td><ul><li>饮酒后开车（记12分，扣驾驶证6个月）</li><li>再次饮酒后开（拘留10日，吊销驾驶证且2年内禁驾）</li></ul></td></tr><tr><td>2k以下</td><td><ul><li>考试过程中有贿赂、舞弊行为</li><li>以欺骗、贿赂等不正当手段取得驾驶证</li><li>代替实际机动车驾驶人参加审验教育</li></ul></td></tr><tr><td rowspan="2">5k 区间</td><td>2k元～5k元</td><td><ul><li>伪造、变造或使用伪造、变造的机动车驾驶证书、号牌、行驶证、驾驶证</li><li>使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志</li></ul></td></tr><tr><td>5k元</td><td>酒后驾驶营运机动车（拘留15日、吊销机动车驾驶证、5年内不得重新取得驾照）</td></tr><tr><td>2w 区间</td><td>3倍以上～2w</td><td> 组织他人代替参加审验教育 </td></tr><tr><td>5w 区间</td><td>3倍以上～5w</td><td>请他人代接受为法处罚和记分</td></tr></tbody></table><h2 id="教育和减分" tabindex="-1">教育和减分 <a class="header-anchor" href="#教育和减分" aria-label="Permalink to &quot;教育和减分&quot;">​</a></h2><h3 id="教育学习" tabindex="-1">教育学习 <a class="header-anchor" href="#教育学习" aria-label="Permalink to &quot;教育学习&quot;">​</a></h3><ul><li>网 5 线 2 不少 7</li></ul><h3 id="学习减分" tabindex="-1">学习减分 <a class="header-anchor" href="#学习减分" aria-label="Permalink to &quot;学习减分&quot;">​</a></h3><table tabindex="0"><thead><tr><th>场景</th><th style="text-align:center;">时间</th><th style="text-align:center;">减少分数</th></tr></thead><tbody><tr><td>网上</td><td style="text-align:center;">30分钟</td><td style="text-align:center;">1分</td></tr><tr><td>公益</td><td style="text-align:center;">1小时</td><td style="text-align:center;">1分</td></tr><tr><td>现场</td><td style="text-align:center;">1小时</td><td style="text-align:center;">2分</td></tr></tbody></table><h2 id="扣分" tabindex="-1">扣分 <a class="header-anchor" href="#扣分" aria-label="Permalink to &quot;扣分&quot;">​</a></h2><h3 id="记分制度" tabindex="-1">记分制度 <a class="header-anchor" href="#记分制度" aria-label="Permalink to &quot;记分制度&quot;">​</a></h3><ul><li>记分周期 12 个月，满分 12 分</li><li>减免记分最高减 6 分</li><li>一个记分周期内，记分未达 12 分：罚款已缴纳则记分清除；罚款未缴纳，计入下一记分周期</li></ul><h3 id="满分处理" tabindex="-1">满分处理 <a class="header-anchor" href="#满分处理" aria-label="Permalink to &quot;满分处理&quot;">​</a></h3><table tabindex="0"><thead><tr><th>车辆类型 \\ 天数</th><th style="text-align:center;">科 1 满分学习最多</th><th style="text-align:center;">每增加 12 分学习</th></tr></thead><tbody><tr><td>小车</td><td style="text-align:center;">60天</td><td style="text-align:center;">+7天</td></tr><tr><td>大车</td><td style="text-align:center;">120天</td><td style="text-align:center;">+30天</td></tr></tbody></table><h3 id="重考规则" tabindex="-1">重考规则 <a class="header-anchor" href="#重考规则" aria-label="Permalink to &quot;重考规则&quot;">​</a></h3><table tabindex="0"><thead><tr><th>记分分数 \\ 重考</th><th style="text-align:center;">科目1</th><th style="text-align:center;">科目2(场地驾驶技能考试)</th><th style="text-align:center;">科目3(道路驾驶技能考试)</th></tr></thead><tbody><tr><td>12 分</td><td style="text-align:center;">✅</td><td style="text-align:center;">❌</td><td style="text-align:center;">❌</td></tr><tr><td>24 分</td><td style="text-align:center;">✅</td><td style="text-align:center;">❌</td><td style="text-align:center;">✅</td></tr><tr><td>36 分</td><td style="text-align:center;">✅</td><td style="text-align:center;">✅</td><td style="text-align:center;">✅</td></tr></tbody></table><h3 id="扣分场景" tabindex="-1">扣分场景 <a class="header-anchor" href="#扣分场景" aria-label="Permalink to &quot;扣分场景&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">口诀</p><ol><li>倒 1 逆 3 占 6 停 9 高逆 12</li><li>假 1 违 3 闯 6 遮 9 伪 12</li><li>不按车道：普 1 高 3</li><li>人超载：七下 3、6、12；七上 6、9、12；校车 6、12</li><li>货超载：1、3、6</li><li>超速：小车普 3、6 高 3、6、12；大车普 1、6、9 高 6、12</li><li>货车长宽高超过规定 1</li><li>未年检：小车 1、大车 3</li><li>不按规则：灯 1 牌 3</li><li>不按规定避让校车 3</li><li>危险未挂标、轻微逃逸、不可解体、不按路线均为 6</li><li>车型不符 9</li><li>谋取利益 12</li><li>逃逸未犯罪，轻伤死亡 12</li></ol></div><ol><li>载人超载</li></ol><table><thead><tr><th>超过核定人数</th><th>7座以下/其他客车</th><th>7座以上</th><th>校车/公路客运汽车/旅游客运汽车（校客旅）</th></tr></thead><tbody><tr class="text-center"><td>20% 以下</td><td>不扣分</td><td>不扣分</td><td>6分</td></tr><tr class="text-center"><td>20% ~ 50%</td><td>3分</td><td>6分</td><td rowspan="3">12分</td></tr><tr class="text-center"><td>50% ~ 100%</td><td>6分</td><td>9分</td></tr><tr class="text-center"><td>100% 以上</td><td>12分</td><td>12分</td></tr></tbody></table><ol start="2"><li>载货超载</li></ol><table tabindex="0"><thead><tr><th>最大允许总质量</th><th>载货汽车</th></tr></thead><tbody><tr><td>未达 30%</td><td>1 分</td></tr><tr><td>30% - 50%</td><td>3 分</td></tr><tr><td>50% 以上</td><td>6 分</td></tr></tbody></table><ol start="3"><li>超速</li></ol><table><thead><tr><th colspan="2">超速</th><th>小车</th><th>大车</th></tr></thead><tbody><tr><td rowspan="3">普通道路</td><td>10% - 20%</td><td rowspan="2">3分</td><td>1分</td></tr><tr><td>20% - 50%</td><td>6分</td></tr><tr><td>50%以上</td><td>6分</td><td>9分</td></tr><tr><td rowspan="3">高速公路</td><td>10% - 20%</td><td>3分</td><td>6分</td></tr><tr><td>20% - 50%</td><td>6分</td><td rowspan="2">12分</td></tr><tr><td>50%以上</td><td>12分</td></tr></tbody></table><h2 id="限速和车距" tabindex="-1">限速和车距 <a class="header-anchor" href="#限速和车距" aria-label="Permalink to &quot;限速和车距&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">口诀</p><ol><li>无中线：城 3 公 4；同向一道：城 5 公 7</li><li>头转弯下陡坡、冰雪泥泞慢行驶、牵引发动机、故障机动车、进出非机动、路过铁道口窄路和窄桥，行驶各不超 30</li><li>高速无标牌：2 车道 100、60；3 车道 110、90、60；4 车道 110、90、90、60</li><li>能见度：261、145、520(驶离)</li><li>特殊天气 50、特殊路 30</li><li>高速 100 车距 100、低于 100 车距 50</li></ol></div><h2 id="限重" tabindex="-1">限重 <a class="header-anchor" href="#限重" aria-label="Permalink to &quot;限重&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">口诀</p><p>载货汽车：&lt;=49 吨</p></div><h2 id="让行" tabindex="-1">让行 <a class="header-anchor" href="#让行" aria-label="Permalink to &quot;让行&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">口诀</p><ol><li>右让左</li><li>左让直</li><li>转弯让直行</li><li>行人等</li></ol></div><h2 id="停车" tabindex="-1">停车 <a class="header-anchor" href="#停车" aria-label="Permalink to &quot;停车&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">口诀</p><ol><li>口 5 站 3 不得停</li><li>高速警告路障后 150m</li></ol></div><h2 id="道路通行" tabindex="-1">道路通行 <a class="header-anchor" href="#道路通行" aria-label="Permalink to &quot;道路通行&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">口诀</p><ol><li>无车道划分：车在中间通行，行人和非机动车在两侧</li><li>高速车道：同方向划有 2 条以上机动车道，则左快右慢</li></ol></div><h2 id="交通标志" tabindex="-1">交通标志 <a class="header-anchor" href="#交通标志" aria-label="Permalink to &quot;交通标志&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">口诀</p><ol><li>标牌：红高、蓝低、黄建议</li><li>路面：黄高、白低、黑建议</li><li>停车让行：三角减速、八边停</li><li>警告弯路：一急、二反、三连续</li><li>斜线：左斜入，右斜出</li><li>一个凸起减速丘、一个坑低洼、一个凸中间有空间驼峰桥</li><li>三变两车道变少，二变一合流</li><li>黑电话紧急、白电话救援</li></ol></div><h2 id="指示标志" tabindex="-1">指示标志 <a class="header-anchor" href="#指示标志" aria-label="Permalink to &quot;指示标志&quot;">​</a></h2><ul><li>黄虚线指示，双黄实线禁止，双黄虚线潮汐，菱形纵向减速</li><li>黄线白线都可跨越，黄线双向跨越，白线同向跨越</li><li>白线分同向，黄线隔对向</li><li>两边虚中间实港湾车道；全虚应急；无线错车</li><li>白色单线停止线；白色双线让行线；白色双虚减速让行</li></ul><h2 id="变道" tabindex="-1">变道 <a class="header-anchor" href="#变道" aria-label="Permalink to &quot;变道&quot;">​</a></h2><ul><li>变道前转向灯需持续开启 3s 以上</li><li>观察变道方向车外后视镜和盲区，后面有车且能从后视镜看到全貌，确保安全驶入目标车道</li><li>完成变道后关闭转向灯</li><li>不允许连续变更两条或两条以上车道</li><li>不能妨碍其他车辆正常行驶</li></ul><h2 id="酒驾和醉驾" tabindex="-1">酒驾和醉驾 <a class="header-anchor" href="#酒驾和醉驾" aria-label="Permalink to &quot;酒驾和醉驾&quot;">​</a></h2><ul><li>醉驾：血液酒精含量超过 80mg/100ml</li><li>酒驾：血液酒精含量超过 20mg/100ml</li></ul><h2 id="疲劳驾驶" tabindex="-1">疲劳驾驶 <a class="header-anchor" href="#疲劳驾驶" aria-label="Permalink to &quot;疲劳驾驶&quot;">​</a></h2><ul><li>开 4 小时，休 20 分</li></ul><h2 id="判刑" tabindex="-1">判刑 <a class="header-anchor" href="#判刑" aria-label="Permalink to &quot;判刑&quot;">​</a></h2><ul><li>因违规致损（公私财产或人命），3年内</li><li>出事后逃逸，3-7年</li><li>因逃逸致*，7年以上</li><li>组织作弊，情节严重，3-7年</li></ul><h2 id="车内" tabindex="-1">车内 <a class="header-anchor" href="#车内" aria-label="Permalink to &quot;车内&quot;">​</a></h2><h3 id="踏板" tabindex="-1">踏板 <a class="header-anchor" href="#踏板" aria-label="Permalink to &quot;踏板&quot;">​</a></h3><ul><li>左离合</li><li>中制动（刹车）</li><li>右加速</li></ul><h3 id="转向灯" tabindex="-1">转向灯 <a class="header-anchor" href="#转向灯" aria-label="Permalink to &quot;转向灯&quot;">​</a></h3><ul><li>下左上右</li><li>入打左，出打右</li></ul><h3 id="点火开关" tabindex="-1">点火开关 <a class="header-anchor" href="#点火开关" aria-label="Permalink to &quot;点火开关&quot;">​</a></h3><table tabindex="0"><thead><tr><th>英文缩写</th><th>中文名称</th></tr></thead><tbody><tr><td>LOCK</td><td>切断电源，锁方向盘</td></tr><tr><td>ACC</td><td>接通附件电源，如收音机</td></tr><tr><td>ON</td><td>接通除起动机外的全部电源</td></tr><tr><td>START</td><td>接通启动机电源，启动发动机</td></tr></tbody></table><h3 id="系统缩写" tabindex="-1">系统缩写 <a class="header-anchor" href="#系统缩写" aria-label="Permalink to &quot;系统缩写&quot;">​</a></h3><table tabindex="0"><thead><tr><th>英文缩写</th><th>中文名称</th></tr></thead><tbody><tr><td>BSD</td><td>盲点检测系统</td></tr><tr><td>AEB</td><td>自动紧急制动系统</td></tr><tr><td>FCW</td><td>前方碰撞预警系统</td></tr><tr><td>ACC</td><td>自适应巡航系统</td></tr><tr><td>ALC</td><td>辅助驾驶系统</td></tr><tr><td>TSR</td><td>交通标志识别系统</td></tr><tr><td>TMC</td><td>交通信息频道</td></tr><tr><td>LDW</td><td>车道偏离预警系统</td></tr></tbody></table>',59),r=[e];function o(h,s,n,c,u,b){return l(),d("div",null,r)}const m=t(i,[["render",o]]);export{p as __pageData,m as default};
